function hariIndo($hariInggris)
{
    switch ($hariInggris) {
        case 'Sunday':
            return 'Minggu';
        case 'Monday':
            return 'Senin';
        case 'Tuesday':
            return 'Selasa';
        case 'Wednesday':
            return 'Rabu';
        case 'Thursday':
            return 'Kamis';
        case 'Friday':
            return 'Jumat';
        case 'Saturday':
            return 'Sabtu';
        default:
            return 'hari tidak valid';
    }
}

if (!function_exists('bulanIndo')) {
    function bulanIndo($bulanAngka)
    {
        $bulanAngka = (int)$bulanAngka;
        $bulan = array(
            'Januari',
            'Februari',
            'Maret',
            'April',
            'Mei',
            'Juni',
            'Juli',
            'Agustus',
            'September',
            'Oktober',
            'November',
            'Desember'
        );

        return $bulan[$bulanAngka - 1];
    }
}

if (!function_exists('user_agent')) {
    function user_agent($str)
    {
        if (strpos($str, 'MSIE') !== FALSE)
            echo 'Internet explorer';
        elseif (strpos($str, 'Trident') !== FALSE) //For Supporting IE 11
            echo 'Internet explorer';
        elseif (strpos($str, 'Firefox') !== FALSE)
            echo 'Mozilla Firefox';
        elseif (strpos($str, 'Chrome') !== FALSE)
            echo 'Google Chrome';
        elseif (strpos($str, 'Opera Mini') !== FALSE)
            echo "Opera Mini";
        elseif (strpos($str, 'Opera') !== FALSE)
            echo "Opera";
        elseif (strpos($str, 'Safari') !== FALSE)
            echo "Safari";
        else
            echo 'Something else';
    }
}


if (!function_exists('space')) {
    function space($str)
    {
        if (empty($str)) {
            return;
        }
        return str_replace('-', ' ', $str);
    }
}

if (!function_exists('no_space')) {
    function no_space($str)
    {
        if (empty($str)) {
            return;
