if (!function_exists('separator_to_list')) {
    function separator_to_list($str)
    {
        $arr = explode('|', $str);
        $li = '<ul>';
        foreach ($arr as $value) {
            $li .= '<li>' . $value . '</li>';
        }
        $li .= '</ul>';
        return $li;
    }
}

