}

if (!function_exists('tambah_nol')) {
    function tambah_nol($number, $total)
    {
        $jumlah_nol = strlen($number);
        $angka_nol  = $total - $jumlah_nol;
        $nol = "";
        for ($i = 1; $i <= $angka_nol; $i++) {
            $nol .= '0';
        }
        return $nol . $number;
    }
}

