if (!function_exists('datesubstr')) {
    function datesubstr($date)
    {
        $y = substr($date, 0, 4);
        $m = substr($date, 4, 2);
        $d = substr($date, 6, 2);
        return $y . "-" . $m . "-" . $d;
    }
}

